<style>
	.small-cloud {
		animation: fastCloud 10s infinite linear;
	}

	@keyframes fastCloud {
		from {
			transform: translateX(-10px);
		}
		to {
			transform: translateX(45px);
		}
	}

	.big-cloud {
		animation: slowCloud 15s infinite linear;
	}

	@keyframes slowCloud {
		from {
			transform: translate(-40px);
		}
		to {
			transform: translate(30px);
		}
	}
</style>
